INSERT INTO `wp_posts` VALUES(9, 1, '2011-09-19 23:47:04', '2011-09-19 23:47:04', '[lulacruza-icon name="circle-star"]\r\na film by\r\n<span class="sans">VINCENT MOON</span>\r\n<div class="giant-title">ESPERANDO<br><span class="italic">el</span> TSUNAMI</div>an album by\r\n<span class="sans">LULACRUZA</span>\r\n<p class="bigger">A traveling visual album that lies\r\nsomewhere between music and cinema.</p>\r\n<p>Evolving from contemporary ideas of field recordings, this film is a quest for the sounds and\r\npeople of present-day Columbia. and a continuous improvisation by the filmmaker and\r\nmusicians as we compose and create on the road.</p>\r\n[lulacruza-icon name="wave"]\r\n[lulacruza-latest-news-item title="latest news" fallback="no news found at this time" more="..."]\r\n[lulacruza-btn-top title="top"]', 'The Film', '', 'publish', 'open', 'open', '', 'film', '', '', '2011-10-01 01:32:01', '2011-10-01 01:32:01', '', 2, 'http://localhost:8888/lulacruza/?page_id=9', 1, 'page', '', 0);
INSERT INTO `wp_posts` VALUES(10, 1, '2011-09-19 23:46:12', '2011-09-19 23:46:12', '<img src="">', 'The Film', '', 'inherit', 'open', 'open', '', '9-revision', '', '', '2011-09-19 23:46:12', '2011-09-19 23:46:12', '', 9, 'http://localhost:8888/lulacruza/9-revision/', 0, 'revision', '', 0);
